import React, { useState } from 'react';
import axios from 'axios';
import './LeaveForm.css'; // Optional: If you're using separate styles for this page

const LeaveForm = () => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [reason, setReason] = useState('');

  const handleSubmit = async () => {
    if (!from || !to || !reason) {
      alert('Please fill all fields');
      return;
    }

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user._id) {
      alert('User not logged in');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/leave/apply', {
        employeeId: user._id,
        from,
        to,
        reason,
      });

      alert('✅ Leave applied successfully');
      setFrom('');
      setTo('');
      setReason('');
    } catch (error) {
      console.error('Error applying leave:', error.response?.data || error.message);

      const message =
        error.response?.data?.error ||
        error.response?.data?.message ||
        '❌ Failed to apply leave. Try again.';

      alert(message);
    }
  };

  return (
    <div className="form-container">
      <h2>Apply Leave</h2>

      <input
        type="date"
        value={from}
        onChange={(e) => setFrom(e.target.value)}
        placeholder="From Date"
        autoComplete="off"
        className="form-input"
      /><br />

      <input
        type="date"
        value={to}
        onChange={(e) => setTo(e.target.value)}
        placeholder="To Date"
        autoComplete="off"
        className="form-input"
      /><br />

      <textarea
        placeholder="Reason for leave"
        value={reason}
        onChange={(e) => setReason(e.target.value)}
        rows={4}
        className="form-textarea"
      /><br />

      <button onClick={handleSubmit} className="form-button">
        Submit
      </button>
    </div>
  );
};

export default LeaveForm;
