import React, { useState } from 'react';
import axios from 'axios';
import './LeaveForm.css';

const LeaveForm = () => {
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [reason, setReason] = useState('');

  const handleSubmit = async () => {
    if (!fromDate || !toDate || !reason) {
      alert('‚ö†Ô∏è Please fill in all fields');
      return;
    }

    if (new Date(fromDate) > new Date(toDate)) {
      alert('‚ö†Ô∏è From Date cannot be later than To Date');
      return;
    }

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user?._id) {
      alert('‚ö†Ô∏è User not logged in');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/leave/apply', {
        employeeId: user._id,
        fromDate,
        toDate,
        reason,
      });

      alert('‚úÖ Leave applied successfully!');
      setFromDate('');
      setToDate('');
      setReason('');
    } catch (error) {
      console.error('‚ùå Error applying leave:', error.response?.data || error.message);
      alert(error.response?.data?.error || 'Failed to apply for leave.');
    }
  };

  return (
    <div className="form-container">
      <h2>üìù Apply Leave</h2>
      <input
        type="date"
        value={fromDate}
        onChange={(e) => setFromDate(e.target.value)}
        className="form-input"
        placeholder="From Date"
      /><br />
      <input
        type="date"
        value={toDate}
        onChange={(e) => setToDate(e.target.value)}
        className="form-input"
        placeholder="To Date"
      /><br />
      <textarea
        value={reason}
        onChange={(e) => setReason(e.target.value)}
        className="form-textarea"
        placeholder="Reason for leave"
        rows={4}
      /><br />
      <button onClick={handleSubmit} className="form-button">
        Submit
      </button>
    </div>
  );
};

export default LeaveForm;
